import React, { useState } from "react";
import axios from "axios";

export default function FilesUploadComponent() {
  const [imgCollection, setImgCollection] = useState("");
  const [isFileUploaded, setIsFileUploaded] = useState(false);
  const [uploadedFiles, setUploadedFiles] = useState([]); // New state to store uploaded files

  const onFileChange = (e) => {
    setImgCollection(e.target.files);
  };

  const onSubmit = (e) => {
    e.preventDefault();

    var formData = new FormData();
    for (const key of Object.keys(imgCollection)) {
      formData.append("imgCollection", imgCollection[key]);
    }

    axios
      .post("http://localhost:4000/api/upload-images", formData)
      .then((res) => {
        // Assuming the response contains a list of uploaded file URLs or names
        setUploadedFiles(res.data.files); // Update the uploaded files state
        setIsFileUploaded(true);
      })
      .catch((err) => {
        console.error("Error uploading files", err);
        setIsFileUploaded(false);
      });
  };

  return (
    <div className="container">
      <h2 className="mb-3">File Upload</h2>
      <form onSubmit={onSubmit}>
        <div className="mb-3">
          <input
            type="file"
            className="form-control"
            name="imgCollection"
            onChange={onFileChange}
            multiple
          />
        </div>
        <button className="btn btn-primary" type="submit">
          Upload
        </button>
      </form>

      {isFileUploaded && (
        <div className="alert alert-success mt-3">
          Files successfully uploaded!
        </div>
      )}

      {/* Display uploaded files */}
      {uploadedFiles.length > 0 && (
        <div className="mt-4">
          <h4>Uploaded Files:</h4>
          <ul>
            {uploadedFiles.map((file, index) => (
              <li key={index}>
                <a href={file.url} target="_blank" rel="noopener noreferrer">
                  {file.name} {/* Assuming the file object contains name and url */}
                </a>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
